set -o errexit
set -o nounset
set -o pipefail

# vars

# run code
krun::deploy::sshkey::run() {
    # default platform
    platform='debian'
    # command -v apt >/dev/null && platform='debian'
    command -v yum >/dev/null && platform='centos'
    command -v brew >/dev/null && platform='mac'
    eval "${FUNCNAME/::run/::${platform}}"
}

# centos code
krun::deploy::sshkey::centos() {
    krun::deploy::sshkey::common
}

# debian code
krun::deploy::sshkey::debian() {
    krun::deploy::sshkey::common
}

# mac code
krun::deploy::sshkey::mac() {
    krun::deploy::sshkey::common
}

# common code
krun::deploy::sshkey::common() {
    key='ssh-rsa '
    ops_lab_key='ssh-rsa  root@ops-lab'
    grep -q "${key}" ~/.ssh/authorized_keys || echo "${key}" >>~/.ssh/authorized_keys
    grep -q "${ops_lab_key}" ~/.ssh/authorized_keys || echo "${ops_lab_key}" >>~/.ssh/authorized_keys
    echo "sshkey deploy done."
}

# run main
krun::deploy::sshkey::run "$@"
